class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int i,j,m=grid.size(),n=grid[0].size(),curr,ans=0,count=0,onesCount=0;
        for(i=0;i<m/2;i++) {
            for(j=0;j<n/2;j++) {
                curr=grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-i-1][n-j-1];
                if(curr==0||curr==4)
                    continue;
                else if(curr==2)
                    ans+=2;
                else
                    ans+=1;
            }
        }
        // cout<<ans<<endl;
        if(m%2==0&&n%2==0)
            return ans;
        else {
            if(m%2==1) {
                for(j=0;j<n/2;j++)
                    if(grid[m/2][j]!=grid[m/2][n-1-j])
                        count++;
                for(j=0;j<n;j++)
                    if(grid[m/2][j]==1)
                        onesCount++;
            }
            if(n%2==1) {
                for(i=0;i<m/2;i++)
                    if(grid[i][n/2]!=grid[m-1-i][n/2])
                        count++;
                for(i=0;i<m;i++)
                    if(grid[i][n/2]==1)
                        onesCount++;
            }
            if(m%2==1&&n%2==1&&grid[m/2][n/2]==1)
                onesCount--;
            // cout<<count<<" "<<onesCount<<endl;
            if(count==0&&onesCount%4==1)
                return ans+1;
            if(count==0&&onesCount%4==3)
                return ans+3;
            if(count==0&&onesCount%4==2)
                return ans+2;
            if(m%2==0||n%2==0)
                return ans+count;
            if(grid[m/2][n/2]==0)
                return ans+count;
            return ans+count+1;
        }
        return -1;
    }
};
