class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int row=grid.size();
        int col=grid[0].size();
        int cnt=0;
        int yes=0;
        for(int i=0;i<row;i++)
        {
            for(int j=0;j<col/2+1;j++)
            {
                if(grid[i][j]!=grid[i][col-1-j])
                {
                    if(grid[i][j]!=grid[row-1-i][j]){
                        grid[i][j]=grid[row-1-i][j];
                    }
                    else
                    {
                        grid[i][col-1-j]=grid[i][j];
                    }
                    if(2*i+1==row)yes=1;
                    cnt++;
                }
            }
        }
        for(int j=0;j<col;j++){
            for(int i=0;i<row/2+1;i++)
            {
                if(grid[i][j]!=grid[row-1-i][j])
                {
                    if(grid[i][j]!=grid[i][col-1-j])
                    {
                        grid[i][j]=grid[row-1-i][j];
                    }
                    else
                    grid[row-1-i][j]=grid[i][j];
                    if(2*j+1==col)yes=1;
                    cnt++;
                }
            }
        }
    int ones=0;
        for(int i=0;i<row;i++){
            for(int j=0;j<col;j++)
            {
                if(grid[i][j]==1)ones++;
            }
        }
        ones=ones%4;
        if(ones==1)
        {
            cnt++;
        }
        else if(ones==2)
        {
            cnt=cnt+2;
        }
        else if(ones==3)
        {
            cnt=cnt+3;
        }
        if(ones==2 || ones==3)
        {

            if(yes==1){
                cnt=cnt-2;
            }

        }
        return cnt;
    }
};
