class Solution {
public:
    int minFlips(vector<vector<int>>& a1) {

        int n = a1.size();
        int m = a1[0].size();

        int row1 = 0, row2 = n - 1, col1 = 0, col2 = m - 1;

        vector<pair<int, pair<int, int>>> vec;

        while(col1 <= col2) {
            row1 = 0;
            row2 = n - 1;


            while(row1 <= row2) {

                set<pair<int, int>> st;

                st.insert({row1, col1});
                st.insert({row1, col2});
                st.insert({row2, col1});
                st.insert({row2, col2});

                int cnt[2] = {0};

                set<pair<int, int>>::iterator it;
                for(it = st.begin(); it != st.end(); it++) {
                    pair<int, int> p = *it;
                    cnt[a1[p.first][p.second]] += 1;
                }

                int d = st.size();
                vec.push_back({d, {cnt[0], cnt[1]}});

                row1++;
                row2--;
            }
            col1++;
            col2--;
        }

        int sz = vec.size();

        int dp[sz + 1][4];

        memset(dp, 1e9, sizeof(dp));

        for(int i = 0; i<= sz; i++) {
            for(int j = 0; j < 4; j++) {
                dp[i][j] = 1e9;
            }
        }

        dp[0][0] = 0;

        for(int i = 1; i <= sz; i++) {
            pair<int, pair<int, int>> p = vec[i - 1];
            int d = p.first, zero = p.second.first, one = p.second.second;

            for(int j = 0; j < 4; j++) {
                if(dp[i - 1][j] != 1e9) {
                    int r1 = (j + d) % 4, r2 = j;
                    dp[i][r1] = min(dp[i][r1], dp[i - 1][j] + (d - one));
                    dp[i][r2] = min(dp[i][r2], dp[i - 1][j] + (d - zero));
                }
            }
        }

        return dp[sz][0];

    }
};
