class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(), m=grid[0].size();
        int rowFlip = 0;
        int cnt1=0;
        int mark[n][m];
        int ans=0;
        memset(mark,0,sizeof mark);
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                vector<int> elements = {
                    grid[i][j],
                    grid[n - i - 1][j],
                    grid[i][m - j - 1],
                    grid[n - i - 1][m - j - 1]
                };

                int ones=0;
                for(auto v:elements){
                    ones+=v;
                }
                ans += min(ones, 4 - ones);
            }
        }
        int possible = 0,count=0;
        bool isOdd = false;
        if(n%2) isOdd = true;
        if (isOdd) {
            int rn = n/2;
            for (int j = 0; j < m / 2; ++j) {
                int lc = m-j-1;
                if (grid[rn][j] != grid[rn][lc]) {
                    ans += 1;
                    possible += 1;
                } else {
                    if (grid[n / 2][j] == 1) {
                        count += 1;
                    }
                }
            }
        }
        isOdd = false;
        if(m%2) isOdd = true;
        if (isOdd) {
            for (int i = 0; i < n / 2; ++i) {
                int lr = n-i-1;
                if (grid[i][m / 2] != grid[lr][m / 2]) {
                    ans += 1;
                    possible += 1;
                } else {
                    if (grid[i][m / 2] == 1) {
                        count += 1;
                    }
                }
            }
        }
        bool isrOdd = false;
        bool iscOdd = false;
        if(n%2) isrOdd = true;
        if(m%2) iscOdd = true;
        if (isrOdd && iscOdd && grid[n / 2][m / 2] == 1) {
            ans += 1;
        }

        bool oneC = false;
        bool twoC = false;
        if(possible==0) oneC = true;
        if(count%2) twoC = true;
        if (oneC) {
            if(twoC) ans += 2;
        }

        return ans;

    }
};
