class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        flips = 0
        for i in range(m//2):
            for j in range(n//2):
                sum = grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j]
                if sum == 1 or sum == 3:
                    flips += 1
                elif sum == 2:
                    flips += 2

        total1, flips1 = 0, 0
        if m%2:
            i = m//2
            for j in range(n//2):
                if grid[i][j] != grid[i][n-1-j]:
                    flips1 += 1
                elif grid[i][j] == 1:
                    total1 += 2

        if n%2:
            j = n//2
            for i in range(m//2):
                if grid[i][j] != grid[m-1-i][j]:
                    flips1 += 1
                elif grid[i][j] == 1:
                    total1 += 2

        flips += flips1
        if total1%4 == 2:
            if not flips1:
                flips += 2

        if m%2 and n%2 and grid[m//2][n//2] == 1:
            flips += 1

        return flips
