class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int result = 0;

        if(n%2 && m%2 && grid[m/2][n/2])
            result++;

        vector<int> temp(3, 0);
        if(m%2){
            int i = 0, j= n-1;
            while(i<j){
                int curr = grid[m/2][i] + grid[m/2][j];
                temp[curr]++;
                i++, j--;
            }
        }

        if(n%2){
            int i = 0, j = m - 1;
            while(i<j){
                int curr = grid[i][n/2]+ grid[j][n/2];
                temp[curr]++;
                i++, j--;
            }
        }

        if(temp[2]%2){
            if(temp[1]>0)
                result += temp[1];
            else
                result += 2;
        } else{
            result += temp[1];
        }

        for(int i = 0; i<m/2; i++){
            for(int j = 0; j<n/2; j++){
                int tl = grid[i][j];
                int bl = grid[i][n-j-1];
                int tr = grid[m - i -1][j];
                int br = grid[m -i-1][n-j-1];

                int net = tl+bl+br+tr;
                if(net && net !=4)
                    result += min(net, 4-net);
            }
        }

        return result;
    }
};
