class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size();
        int m = grid[0].size();

        int res = 0;

        for(int i = 0; i < n/2; ++i) {
            for(int j = 0; j < m/2; ++j) {
                int t = grid[i][j] + grid[n-i-1][j] + grid[i][m-j-1] + grid[n-i-1][m-j-1];
                if(t%4 != 0) {
                    res += min(t, 4-t);
                }
            }
        }

        int y = 0;
        int x = 0;
        if(n%2 == 1) {
            int s = 0;
            int e = m-1;
            while(s < e) {
                if(grid[n/2][s] != grid[n/2][e]) y++;
                else if(grid[n/2][s] == 1) x++;
                s++;
                e--;
            }

        }

        if(m%2 == 1) {
            int s = 0;
            int e = n-1;
            while(s < e) {
                if(grid[s][m/2] != grid[e][m/2]) y++;
                else if(grid[s][m/2] == 1) x++;
                s++;
                e--;
            }
        }

        x = 2*x;
        res += y;

        if((x%4 == 2) && (y == 0)) res += 2;

        if((n%2 == 1) && (m%2 == 1)) res += grid[n/2][m/2];

        return res;

    }
};
