#include <vector>
#include <algorithm>
using namespace std;
int n,m;
class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        n=g.size();
        m=g[0].size();
        int res=0;

        for(int i=0;i<n/2;++i) {
            for(int j=0;j<m/2;++j) {
                int a=g[i][j],b=g[n-i-1][j],c=g[i][m-j-1],d=g[n-i-1][m-j-1];
                vector<int> v={a,b,c,d};
                int cnt=0;
                for(auto& x:v) {
                    if(x==1) cnt++;
                }
                res+=min(cnt,4-cnt);
            }
        }

        int pos=0,oneCnt=0;

        if(m%2!=0) {

            for(int i=0;i<n/2;++i) {

                if(g[i][m/2]!=g[n-i-1][m/2])
                {
                    res+=1;pos+=1;
                } 
                else if(g[i][m/2]==1) {
                    oneCnt+=1;
                }
            }
        }

        if(n%2!=0) {

            for(int j=0;j<m/2;++j) {

                if(g[n/2][j]!=g[n/2][m-j-1]) {

                    res+=1;pos+=1;

                } 
                else if(g[n/2][j]==1) {
                    oneCnt+=1;
                }
            }
        }


        if(m%2==3) {

            for(int i=0;i<n/2;++i) {

                if(g[i][m/2]!=g[n-i-1][m/2])
                {
                    res+=1;pos+=1;
                } 
                else if(g[i][m/2]==1) {
                    oneCnt+=1;
                }
            }
        }
        else if(n%2==3) {

            for(int i=0;i<n/2;++i) {

                if(g[i][m/2]!=g[n-i-1][m/2])
                {
                    res+=1;pos+=1;
                } 
                else if(g[i][m/2]==1) {
                    oneCnt+=1;
                }
            }
        }

        if(n%2==1 && m%2==1 && g[n/2][m/2]!=0) {
            res+=1;
        }
        if(pos==0 && oneCnt%2==1) {
            res+=2;
        }
        return res;
    }
};
