class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int minFlips = 0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int diffs = 0;
                if(grid[i][j]!=grid[i][n-1-j])
                    diffs++;
                if(grid[i][j]!=grid[m-1-i][j])
                    diffs++;
                if(grid[i][j]!=grid[m-1-i][n-1-j])
                    diffs++;
                if(diffs==1 or diffs==3)
                    minFlips++;
                else if(diffs==2)
                    minFlips+=2;
            }
        }
        cout<<"2d minflips: "<<minFlips<<endl;
        if(m%2 and n%2){
            if(grid[m/2][n/2])
                minFlips++;
            int m1=0, nmc=0;
            for(int j=0;j<n/2;j++){
                if(grid[m/2][j]==grid[m/2][n-1-j]){
                    if(grid[m/2][j]==1)
                        m1++;
                }
                else
                    nmc++;
            }
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]==grid[m-1-i][n/2]){
                    if(grid[i][n/2]==1)
                        m1++;
                }
                else
                    nmc++;
            }
            if((nmc+2*m1)%4==0){
                minFlips+=nmc;
            }else if(nmc==0)
                minFlips+=2;
            else
                minFlips+=nmc;
            cout<<"both odd: "<<minFlips<<endl;
        }
        else if(m%2){
            int m1=0, nmc=0;
            for(int j=0;j<n/2;j++){
                if(grid[m/2][j]==grid[m/2][n-1-j]){
                    if(grid[m/2][j]==1)
                        m1++;
                }
                else
                    nmc++;
            }
            if((nmc+2*m1)%4==0){
                minFlips+=nmc;
            }else if(nmc==0)
                minFlips+=2;
            else
                minFlips+=nmc;
            cout<<"odd rows: "<<minFlips<<endl;
        }
        else if(n%2){
            int m1=0, nmc=0;
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]==grid[m-1-i][n/2]){
                    if(grid[i][n/2]==1)
                        m1++;
                }
                else
                    nmc++;
            }
            if((nmc+2*m1)%4==0){
                minFlips+=nmc;
            }else if(nmc==0)
                minFlips+=2;
            else
                minFlips+=nmc;
            cout<<"odd cols: "<<minFlips<<endl;
        }
        return minFlips;
    }
};
