class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int m=grid.size(),n=grid[0].size();
        for(int i=0;i<m/2;i++)
        {
            for(int j=0;j<n/2;j++)
            {
                int one=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                if(one==2)
                    ans+=2;
                if(one==1 || one==3)
                    ans+=1;
            }
        }
        if(m%2==0 && n%2==0)
        return ans;

        if(m%2!=0 && n%2!=0)
        {
             int x=m/2,y=n/2;
            if(m%2!=0 && n%2!=0 && grid[x][y]==1)
            ans++;

             int c=0;
            int one=0;
              for(int i=0;i<n/2;i++)
             {
                if(grid[x][i]!=grid[x][n-1-i])
                c++;
                if(grid[x][i]==1)
                one++;
             }

             for(int i=0;i<m/2;i++)
            {
                if(grid[i][y]!=grid[m-1-i][y])
                c++;
                if(grid[i][y]==1)
                one++;
            }
             if(c==0 && one%2!=0)
            ans+=2;
            else
            ans+=c;

            return ans;

        }
        if(m%2!=0)
        {
            int x=m/2;
