class Solution {
public: // code written by sunny...
    int minFlips(vector<vector<int>>& grid) {
        int p = grid.size(), k = grid[0].size();
        int ans = 0;
        if (p % 2 == 1 && k % 2 == 1) {
            if (grid[p / 2][k / 2] == 1) {
                ans += 1;
            }
        }
        vector<int> shift(3, 0);
        if (p % 2 == 1) {
            int x = 0, y = k - 1;
            while (x < y) {
                int sm = grid[p / 2][x] + grid[p / 2][y];
                shift[sm] += 1;
                x += 1;
                y -= 1;
            }
        }
        if (k % 2 == 1) {
            int x = 0, y = p - 1;
            while (x < y) {
                int sm = grid[x][k / 2] + grid[y][k / 2];
                shift[sm] += 1;
                x += 1;
                y -= 1;
            }
        }
        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) {
                ans += shift[1];
            } else {
                ans += 2;
            }
        } else {
            ans += shift[1];
        }
        for (int t = 0; t < p / 2; ++t) {
            for (int z = 0; z < k / 2; ++z) {
                int x = grid[t][z];
                int y = grid[p - 1 - t][z];
                int c = grid[t][k - 1 - z];
                int d = grid[p - 1 - t][k - 1 - z];
                int sm = x + y + c + d;
                if (sm != 0 && sm != 4) {
                    ans += min(sm, 4 - sm);
                }
            }
        }
        return ans;
    }
};
