class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int ans=0,m=g.size(),n=g[0].size();
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int i1=i,j1=j;
                int i2=i,j2=n-j-1;
                int i3=m-i-1,j3=j;
                int i4=m-i-1,j4=n-j-1;
                int cnt1=0,cnt0=0;
                cnt1+=g[i1][j1]==1; cnt1+=g[i2][j2]==1; cnt1+=g[i3][j3]==1; cnt1+=g[i4][j4]==1;
                cnt0+=g[i1][j1]==0; cnt0+=g[i2][j2]==0; cnt0+=g[i3][j3]==0; cnt0+=g[i4][j4]==0;
                // cout<<cnt0<<" "<<cnt1<<endl;
                ans+=(4-max(cnt0,cnt1));
            }
        }
        int may=0,force=0;
        if(m%2==1){
            int l=0,r=n-1;
            while(l<r){
                if(g[m/2][l]!=g[m/2][r]){
                    ans++; may++;
                }else if(g[m/2][l]==1){
                    force++;
                }
                l++; r--;
            }
        }
        if(n%2==1){
            int l=0,r=m-1;
            while(l<r){
                if(g[l][n/2]!=g[r][n/2]){
                    ans++; may++;
                }else if(g[l][n/2]==1){
                    force++;
                }
                l++; r--;
            }
        }
        if(m%2==1 && n%2==1 && g[m/2][n/2]==1) ans++;
        if(force%2==1){
            if(may>=1) return ans;
            else{
                ans+=2; return ans;
            }
        }
        return ans;
    }
};
