class Solution {
    public int minFlips(int[][] grid) {

       int rows = grid.length ;
       int cols  = grid[0].length ;

       int ans = 0;


  for(int c = 0 ; c < cols/2 ;c++){
     for(int r = 0 ; r < rows/2 ;r++){

        int tl = grid[r][c] ;
        int tr = grid[r][cols-c-1];
        int bl = grid[rows-1-r][c] ;
        int br  = grid[rows-1-r][cols-c-1]; 

         int ones = tl+tr+bl+br ;
         int zeros = 4-ones ;

          ans+= Math.min(ones,zeros);

     }

  }

   if(((rows &1) == 0) && (cols&1) == 0)return ans;

          int zz= 0 ;
          int oo = 0;
          int oz = 0;

        for(int l= 0 ,r= cols-1; l <= r && (rows&1)==1 ; l++,r--){
            int v1 = grid[rows/2][l] ;
            int v2  = grid[rows/2][r];

            if(v1  == 0 && v2  == 0)zz++;
            else if(v1  == 1 && v2  == 1)oo++;
            else {oz++;ans++ ;}
        }

        for(int  u= 0 ,d= rows-1; u <= d && (cols&1)==1; u++,d--){
            int v1 = grid[u][cols/2] ;
            int v2  = grid[d][cols/2];

            if(v1  == 0 && v2  == 0)zz++;
            else if(v1  == 1 && v2  == 1)oo++;
            else {oz++;ans++ ;}
        }

       if(((rows&1) == 1) && ((cols&1) == 1)){
        if(grid[rows/2][cols/2] == 1)ans++;
       }

        if((oo & 1) == 0)return ans ;
        else {
            if(oz > 0)return ans ;
            else{
                ans+=2;
                return ans ;
            }
        }


    }
}
