class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0, ones = 0;
        for(int i=0; i<grid.size(); i++)
            for(int j=0; j<grid[i].size(); j++)
                if(grid[i][j])
                    ones++;

        for(int i=0, l=grid.size()-1; i<l; i++, l--){
            for(int j=0, k=grid[i].size()-1; j<k; j++, k--){
                int a = grid[i][j];
                int b = grid[i][k];
                int c = grid[l][j];
                int d = grid[l][k];
                int sum = a + b + c + d;
                if(sum%4==1) ones--, ans++;
                if(sum%4==2) ones+=2, ans+=2;
                if(sum%4==3) ones++, ans++;
            }
        }

        int cnt1 = 0, cnt2 = 0;
        int sum = 0;
        if(grid.size()%2){
            int i=grid.size()/2;
            for(int j=0, k=grid[i].size()-1; j<k; j++, k--){
                if(grid[i][j]!=grid[i][k])
                    cnt1++, ans++;
                if(grid[i][j]==grid[i][k] && grid[i][j]==1)
                    sum += 2;
            }
        }

        if(grid[0].size()%2){
            int j=grid[0].size()/2;
            for(int i=0, k=grid.size()-1; i<k; i++, k--){
                if(grid[i][j]!=grid[k][j])
                    cnt2++, ans++;
                if(grid[i][j]==grid[k][j] && grid[i][j]==1)
                    sum += 2;
            }
        }

        if(grid.size()%2 && grid[0].size()%2){
            ans += grid[grid.size()/2][grid[0].size()/2];
            ones -= grid[grid.size()/2][grid[0].size()/2];
        }

        if(sum%4==2){
            if(cnt1 || cnt2) ones += 2;
            else ones -= 2, ans += 2;
        }

        return ans;
    }
};
