class Solution {
public:

    int minFlips(vector<vector<int>>& grid) {

    int n = grid.size(), m = grid[0].size(), ans = 0;

    for (int i = 0; i < n / 2; i++) {
        for (int j = 0; j < m / 2; j++) {
            vector<int> elements = {
                grid[i][j],
                grid[n - i - 1][j],
                grid[i][m - j - 1],
                grid[n - i - 1][m - j - 1]
            };

            int ones = elements[0] + elements[1] + elements[2] + elements[3];

            ans += min(ones, 4 - ones);
        }
    }

    int temp = 0, count = 0;

    if (n % 2 != 0) {
        for (int j = 0; j < m / 2; j++) {
            if (grid[n / 2][j] != grid[n / 2][m - j - 1]) {
                ans++;
                temp++;
            }

            else {
                if (grid[n / 2][j] == 1) {
                    count++;
                }
            }
        }
    }

    if (m % 2 != 0) {
        for (int i = 0; i < n / 2; ++i) {
            if (grid[i][m / 2] != grid[n - i - 1][m / 2]) {
                ans++;
                temp++;
            } 

            else {
                if (grid[i][m / 2] == 1) {
                    count++;
                }
            }
        }
    }

    if ((temp == 0) && (count % 2 != 0)) {
        ans += 2;
    }

    if ((n % 2 != 0) && (m % 2 != 0) && (grid[n / 2][m / 2] == 1)) {
        ans++;
    }

    return ans;
    }
};
