class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(),n= grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++)
        {
            for(int j=0;j<n/2;j++)
            {
                int cnt = grid[i][j]+grid[m-1-i][j]+grid[i][n-1-j]+grid[m-1-i][n-1-j];
                ans+= min(cnt,4-cnt);
            }
        }
        int cnt=0;
        int md=0;
        if(m%2)
        {
            int l=0,r=n-1;
            while(l<r)
            {
                if(grid[m/2][l] != grid[m/2][r])
                {
                    ans++;
                    cnt+=2;
                    md++;
                }
                else if(grid[m/2][l] ==1 and 1== grid[m/2][r]) cnt+=2;
                l++;
                r--;
            }
        }
        if(n%2)
        {
            int t=0,b=m-1;
            while(t<b)
            {
                if(grid[t][n/2] != grid[b][n/2])
                {
                     ans++;
                     md++;
                }
                else if(grid[t][n/2] ==1 and 1== grid[b][n/2]) cnt+=2;
                t++;
                b--;
            }

        }
        if(m%2 and n%2 and grid[m/2][n/2]) ans++;
        if(cnt%4!=0)
        {
            if(md==0) ans+=2;
        }
        return ans;
    }
};
