class Solution {
public:
    int minFlips(vector<vector<int>>& grid) 
    {
        int n = grid.size();
        int m = grid[0].size();
        vector<vector<int>> vis(n, vector<int>(m, 0));
        vector<pair<int,int>> cost;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                int cnt = 0;
                int tot=0;
                if (vis[i][j] == 0) {

                    vis[i][j] = 1;
                    if (grid[i][j]) {
                        cnt++;
                    }
                    tot++;
                }
                if (vis[i][m - 1 - j] == 0) {
                    vis[i][m - 1 - j] = 1;
                    if (grid[i][m - 1 - j]) {
                        cnt++;
                    }
                    tot++;
                }

                if (vis[n - 1 - i][j] == 0) {
                    vis[n - 1 - i][j] = 1;
                    if (grid[n - 1 - i][j]) {
                        cnt++;
                    }
                    tot++;
                }

                if (vis[n - 1 - i][m - 1 - j] == 0) {
                    vis[n - 1 - i][m - 1 - j] = 1;
                    if (grid[n - 1 - i][m - 1 - j]) {
                        cnt++;
                    }
                    tot++;
                }

                cost.push_back({cnt,tot});
            }
        }
        for(int i=0;i<cost.size();i++)
        {
            cost[i].second-=cost[i].first;
        }


        vector<pair<int,pair<int,int>>> pp;
        for(int i=0;i<cost.size();i++)
        {
            pp.push_back({cost[i].first+cost[i].second,{cost[i].second,cost[i].first}});
        }
        int ans=0;

        vector<pair<int,int>> v;
        for(auto x:pp)
        {
            if(x.first==4)
            {
                ans+=min(x.second.first,x.second.second);
            }
            if(x.first==2)
            {
                v.push_back({x.second.first,x.second.second});
            }
            if(x.first==1)
            {
                ans+=x.second.second;
            }
        }
        sort(v.begin(),v.end());
        int ans1=0;
        for(auto x:v)
        {
            ans1+=x.second;
        }
        int sum=ans1;
        for(int i=0;i<v.size();i+=2)
        {
            if(i+1<v.size())
            {
                 sum-=v[i].second;
                sum-=v[i+1].second;
                sum+=v[i].first;
                sum+=v[i+1].first;
                   ans1=min(ans1,sum);
            }


        }
       return ans+ans1;
    }
};
