class Solution {
    public int minFlips(int[][] g) {
        int n = g.length;
        int m = g[0].length;
        int a = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (g[n / 2][m / 2] == 1) {
                a += 1;
            }
        }

        int[] s = new int[3];

        if (n % 2 == 1) {
            int x = 0, y = m - 1;
            while (x < y) {
                int sm = g[n / 2][x] + g[n / 2][y];
                s[sm] += 1;
                x += 1;
                y -= 1;
            }
        }

        if (m % 2 == 1) {
            int x = 0, y = n - 1;
            while (x < y) {
                int sm = g[x][m / 2] + g[y][m / 2];
                s[sm] += 1;
                x += 1;
                y -= 1;
            }
        }

        if (s[2] % 2 == 1) {
            if (s[1] > 0) {
                a += s[1];
            } else {
                a += 2;
            }
        } else {
            a += s[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int b = g[i][j];
                int c = g[n - 1 - i][j];
                int d = g[i][m - 1 - j];
                int sm = b + c + d + g[n - 1 - i][m - 1 - j];
                if (sm != 0 && sm != 4) {
                    a += Math.min(sm, 4 - sm);
                }
            }
        }

        return a;
    }
}
