class Solution {
public:
    void ans(vector<vector<int>>& grid)
    {
           int row = grid.size();
        int col = grid[0].size();
        int ans1 = 0, ans2 = 0;
        int ans;


        for (int i = 0; i < row; i++) {
            for (int left = 0, right = col - 1; left < right; left++, right--) {
                if (grid[i][left] != grid[i][right]) {
                    ans1++;
                }
            }
        }


        for (int j = 0; j < col; j++) {
            for (int top = 0, bottom = row - 1; top < bottom; top++, bottom--) {
                if (grid[top][j] != grid[bottom][j]) {
                    ans2++;
                }
            }
        }
        ans=min(ans1,ans2);
    }

    int minFlips(vector<vector<int>>& grid) {

        int rows = grid.size();
        int cols = grid[0].size();
        int result = 0;


        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                result += 1;
            }
        }


        int c0 = 0, c1 = 0, c2 = 0;


        if (rows % 2 == 1) {
            int midRow = rows / 2;
            int left = 0, right = cols - 1;
            while (left < right) {
                int pairSum = grid[midRow][left] + grid[midRow][right];
                if (pairSum == 0) c0++;
                else if (pairSum == 1) c1++;
                else if (pairSum == 2) c2++;
                left++;
                right--;
            }
        }


        if (cols % 2 == 1) {
            int midCol = cols / 2;
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int pairSum = grid[top][midCol] + grid[bottom][midCol];
                if (pairSum == 0) c0++;
                else if (pairSum == 1) c1++;
                else if (pairSum == 2) c2++;
                top++;
                bottom--;
            }
        }


        if (c2 % 2 == 1) {
            if (c1 > 0) {
                result += c1;
            } else {
                result += 2;
            }
        } else {
            result += c1;
        }


        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int tl = grid[i][j];
                int bl = grid[rows - 1 - i][j];
                int tr = grid[i][cols - 1 - j];
                int br = grid[rows - 1 - i][cols - 1 - j];

                int blockSum = tl + bl + tr + br;

                if (blockSum != 0 && blockSum != 4) {
                    result += min(blockSum, 4 - blockSum);
                }
            }
        }

        return result;



    }
};
