class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        rr = 0
        for i in range(len(grid)//2):
            for j in range(len(grid[0])//2):
                ones = 0
                zero = 0
                if grid[i][j]==0:
                    zero += 1
                else:
                    ones += 1
                if grid[len(grid)-i-1][j]==0:
                    zero += 1
                else:
                    ones += 1
                if grid[i][len(grid[0])-j-1]==0:
                    zero += 1
                else:
                    ones += 1
                if grid[len(grid)-i-1][len(grid[0])-j-1]==0:
                    zero += 1
                else:
                    ones += 1
                rr += min(ones, zero)
        flag1 = False
        flag2 = False
        rr2 = 0
        zzx=0
        zzy=0
        if len(grid)%2==1:
            flag1 = True
            for i in range(len(grid[0])//2):

                if grid[len(grid)//2][i]!= grid[len(grid)//2][len(grid[0])-i-1]:
                    rr2 += 1
                elif grid[len(grid)//2][i]==1:
                    zzy +=1


        if len(grid[0])%2==1:
            flag2 = True
            for i in range(len(grid)//2):
                if grid[i][len(grid[0])//2]!= grid[len(grid)-i-1][len(grid[0])//2]:
                    rr2 += 1
                elif grid[i][len(grid[0])//2]==1:
                    zzx += 1
        rr += rr2
        if len(grid)%2==1 and len(grid[0])%2==1 and (grid[len(grid)//2][len(grid[0])//2]==1):
            rr += 1
        if flag1 or flag2:
            if rr2==0 and (zzx + zzy)%2==1:
                rr += 2
        return rr
