class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int r = g.size();
        int c = g[0].size();
        int res = 0;

        for (int i = 0; i < r / 2; ++i) {
            for (int j = 0; j < c / 2; ++j) {
                vector<int> elems = {
                    g[i][j],
                    g[r - i - 1][j],
                    g[i][c - j - 1],
                    g[r - i - 1][c - j - 1]
                };

                int ones = accumulate(elems.begin(), elems.end(), 0);
                res += min(ones, 4 - ones);
            }
        }

        int mid = 0;
        int cnt = 0;

        if (r % 2 != 0) {
            int mRow = r / 2;
            for (int j = 0; j < c / 2; ++j) {
                if (g[mRow][j] != g[mRow][c - j - 1]) {
                    ++res;
                    ++mid;
                } else if (g[mRow][j] == 1) {
                    ++cnt;
                }
            }
        }

        if (c % 2 != 0) {
            int mCol = c / 2;
            for (int i = 0; i < r / 2; ++i) {
                if (g[i][mCol] != g[r - i - 1][mCol]) {
                    ++res;
                    ++mid;
                } else if (g[i][mCol] == 1) {
                    ++cnt;
                }
            }
        }

        if (r % 2 != 0 && c % 2 != 0 && g[r / 2][c / 2] == 1) {
            ++res;
        }

        if (mid == 0 && cnt % 2 != 0) {
            res += 2;
        }

        return res;
    }
};
