from typing import List

class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        flips = 0

        if n % 2 == 1 and m % 2 == 1:
            if grid[n // 2][m // 2] == 1:
                flips += 1

        shifts = [0] * 3

        if n % 2 == 1:
            left, right = 0, m - 1
            while left < right:
                sum_middle_row = grid[n // 2][left] + grid[n // 2][right]
                shifts[sum_middle_row] += 1
                left += 1
                right -= 1

        if m % 2 == 1:
            top, bottom = 0, n - 1
            while top < bottom:
                sum_middle_col = grid[top][m // 2] + grid[bottom][m // 2]
                shifts[sum_middle_col] += 1
                top += 1
                bottom -= 1

        if shifts[2] % 2 == 1:
            if shifts[1] > 0:
                flips += shifts[1]
            else:
                flips += 2
        else:
            flips += shifts[1]

        for i in range(n // 2):
            for j in range(m // 2):
                top_left = grid[i][j]
                top_right = grid[i][m - 1 - j]
                bottom_left = grid[n - 1 - i][j]
                bottom_right = grid[n - 1 - i][m - 1 - j]

                corner_sum = top_left + top_right + bottom_left + bottom_right

                if corner_sum != 0 and corner_sum != 4:
                    flips += min(corner_sum, 4 - corner_sum)

        return flips
