#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size(), cols = grid[0].size();
        int totalFlips = 0;

        // Handle the center cell if grid has odd number of rows and columns
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                totalFlips += 1;
            }
        }

        vector<int> shifts(3, 0);

        // Handle the middle row if grid has an odd number of rows
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                shifts[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        // Handle the middle column if grid has an odd number of columns
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                shifts[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        // Calculate minimum flips for middle rows and columns
        if (shifts[2] % 2 == 1) {
            if (shifts[1] > 0) {
                totalFlips += shifts[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += shifts[1];
        }

        // Handle the rest of the grid
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4) {
                    totalFlips += min(sum, 4 - sum);
                }
            }
        }

        return totalFlips;
    }
};
