#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef long double db;
typedef vector<int> vi;
typedef pair<int, int> pii;
const int N = 200000 + 100;
const db pi = acos(-1.0);
#define lowbit(x) ((x) & (-x))
#define sqr(x) (x) * (x)
#define rep(i,a,b) for (int i = a; i <= b;i++)
#define per(i,a,b) for (int i = a; i >= b;i--)
#define go(x,a) for (auto &x: a)
#define trav(u,i,sq) for (register int i = head[u], v = sq[i].to; i; i = sq[i].nxt, v=sq[i].to)
#define fir first
#define sec second
#define mkp make_pair
#define pb push_back
#define maxd 998244353
#define eps 1e-8

class Solution {
    int f[2][10];
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),ans=0;
		vector<vector<int>> vis(n,vector<int>(m,0));
		int now=1,pre=0;
		rep(i,0,3) f[0][i]=f[1][i]=maxd; f[0][0]=0;
		rep(i,0,(n-1)>>1) rep(j,0,(m-1)>>1) {
			if (vis[i][j] || vis[i][m-j-1] || vis[n-i-1][j] || vis[n-i-1][m-j-1]) continue;
			vis[i][j]=vis[i][m-j-1]=vis[n-i-1][j]=vis[n-i-1][m-j-1]=1;
            //cout<<i<<" "<<j<<endl;
			set<pii> a; a.clear();
			a.insert(mkp(i,j));
			a.insert(mkp(i,m-j-1));
			a.insert(mkp(n-i-1,j));
			a.insert(mkp(n-i-1,m-j-1));
			if (a.size()==4) {
				int cnt[2]={0,0};
				go(x,a) cnt[grid[x.fir][x.sec]]++;
				ans+=min(cnt[0],cnt[1]);
			} else {
				int cnt[2]={0,0},siz=a.size();
				go(x,a) cnt[grid[x.fir][x.sec]]++;
                //cout<<siz<<" "<<cnt[0]<<" "<<cnt[1]<<endl;
                rep(k,0,3) f[now][k]=maxd;
				rep(k,0,3) {
					f[now][(k+siz)%4]=min(f[now][(k+siz)%4],f[pre][k]+cnt[0]);
					f[now][k]=min(f[now][k],f[pre][k]+cnt[1]);
				}
				now^=1; pre^=1;
			}
		}
        //cout<<ans<<endl;
		return ans+f[pre][0];
    }
};
