class Solution {
    public int minFlips(int[][] grid) {
        int totalChannges = 0;
        int n = grid.length;
        int m = grid[0].length;

        if(n==1 && m==1){
            if(grid[0][0]==1){
                return 1;
            }else{
                return 0;
            }
        }

        int rS = 0;
        int rE = n-1;

        while(rS<rE){
            int s = 0;
            int e = m-1;

            while (s<e) {        
