                int count1 = 0;

                if(grid[rS][s]==0){
                    countZ++;
                }else{
                    count1++;
                }

                if(grid[rS][e]==0){
                    countZ++;
                }else{
                    count1++;
                }

                if(grid[rE][s]==0){
