                }else{
                    count1++;
                }

                if(grid[rE][e]==0){
                    countZ++;
                }else{
                    count1++;
                }

                totalChannges += Math.min(count1, countZ);

                e--;
                s++;
            }

            rS++;
            rE--;
        }


        //check if col is mod 2 is 1
        int total1Set = 0;
        int opC = 0;

        if(m%2==1){
            rS = 0;
            rE = n-1;

            int midCol = m/2;
            while (rS<rE) {
                if(grid[rS][midCol]==grid[rE][midCol]){
                    if(grid[rS][midCol]==1){
                        total1Set+=2;
                    }
                }else{
                    opC++;
                }

                rS++;
                rE--;
            }
        }

        if(n%2==1){
            int cS = 0;
            int cE = m-1;

            int midRow = n/2;

            while (cS<cE) {
                if(grid[midRow][cS]==grid[midRow][cE]){
                    if(grid[midRow][cS]==1){
                        total1Set+=2;
                    }
                }else{
                    opC++;
                }

                cS++;
                cE--;
            }

        }

        if(m%2==1 && n%2==1){
            int midCol = m/2;
            int midRow = n/2;

            if(grid[midRow][midCol]==1){
                totalChannges++;
            }
        }

