class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int sol = 0; vector<int>s(3, 0);

        if (n%2==1 && m%2==1) {
            int h=n/2; int l=m/2;
            if (grid[h][l] == 1) {
                sol += 1;
            }
        }


        if (n%2==1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int sk = grid[n / 2][a] + grid[n / 2][b];
                s[sk] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int sk = grid[a][m / 2] + grid[b][m / 2];
                s[sk] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (s[2] % 2 == 1) {
            int p = s[1];
            if (p > 0) {
                sol += s[1];
            } else {
                sol += 2;
            }
        } else {
            sol += s[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n-1-i][j];
                int c = grid[i][m-1-j];
                int d = grid[n-1-i][m-1-j];
                int sk = a+b+c+d;
                if (sk != 0 && sk != 4) {
                    sol += min(sk, 4 - sk);
                }
            }
        }

        return sol;
    }
};
