class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i =0;i<n/2;i++){
           for(int j =0;j<m/2;j++){
               int a = grid[i][j];
               int b = grid[i][m-j-1];
               int c = grid[n-i-1][j];
               int d = grid[n-i-1][m-j-1];
               int cnt1=0,cnt2=0;
               if(a){
                   cnt1++;
               }
               else{
                   cnt2++;
               }
               if(b){
                   cnt1++;
               }
               else{
                   cnt2++;
               }
                if(c){
                   cnt1++;
               }
               else{
                   cnt2++;
               }
                if(d){
                   cnt1++;
               }
               else{
                   cnt2++;
               }
               ans+=min(cnt1,cnt2);
           }
        }
        int ct =0,ct1=0;
        if(n%2 && m%2){
            int rr =0,mm =0;
            int hh =0,rk =0;
             for(int i =0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-i-1][m/2]){
                    rr++;
                }
                 else if(grid[i][m/2] == 1){
                     hh++;
                 }
            }
            for(int i =0;i<m/2;i++){
                if(grid[n/2][i] != grid[n/2][m-i-1]){
                    mm++;
                }
                else if(grid[n/2][i] == 1){
                    rk++;
                }
            }
            if(grid[n/2][m/2] == 1){
                ans++;
            }
            if((hh + rk)%2){
                if(rr+mm == 0){
                    ans+=2;
                }
                else{
                    ans+=rr;
                    ans+=mm;
                }
            }
            else{
                ans+=rr;
                ans+=mm;
        }
        }
        else if(m%2){
            int onec = 0;
            for(int i =0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-i-1][m/2]){
                    ct++;
                }
                else if(grid[i][m/2] == 1){
                    onec++;
                }
            }
            if(onec%2){
                if(ct > 0){
                    ans+=ct;
                }
                else{
                    ans+=2;
                }
            }
            else{
                ans+=ct;
            }
        }
        else if(n%2){
            int ooo =0;
            for(int i =0;i<m/2;i++){
                if(grid[n/2][i] != grid[n/2][m-i-1]){
                    ct1++;
                }
                else if(grid[n/2][i] == 1){
                    ooo++;
                }
            }
            if(ooo%2){
                if(ct1 > 0){
                    ans+=ct1;
                }
                else{
                    ans+=2;
                }
            }
            else{
                ans+=ct1;
            }
        }
        return ans;
    }
};
