class Solution {
    public:
    int minFlips(vector<vector<int>>& grid) {


           int n=grid.size();
           int m=grid[0].size();
           int result=0;
        if(n%2 and m%2)
        {
            if(grid[n/2][m/2])
            result++;
        }

        vector<int> s(3,0);
        if(n%2)
        {
            int aa=0;
            int bb=m-1;

            while(aa<bb)
            {
                int temp=grid[n/2][aa]+grid[n/2][bb];

                s[temp]++;
                aa++;
                bb--;
            }
        }

        if(m%2)
        {
            int aa=0;
            int bb=n-1;

            while(aa<bb)
            {
                int temp=grid[aa][m/2]+grid[bb][m/2];
                s[temp]++;
                aa++;
                bb--;
            }
        }


        if(s[2]%2)
        {
            if(s[1]>0)
            {
                result+=s[1];
            }
            else
            result+=2;
        }
        else
        result+=s[1];


        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int aa=grid[i][j];
                int cc=grid[i][m-1-j];
                int bb=grid[n-1-i][j];
                int dd=grid[n-1-i][m-1-j];

                int temp=aa+bb+cc+dd;
                if(temp and temp!=4)
                result+=min({temp,4-temp});
            }
        }

        return result;
