class Solution {
public:

    int check(vector<int> &v1,vector<int>& v2,int i,vector<int>&dp){
        int n=v1.size();
        if(i>=n/2)return 0;
        if(dp[i]!=-1)return dp[i];
        int ans=v1[i]+v2[i]+v1[n-1-i]+v2[n-1-i]+check(v1,v2,i+1,dp);
        ans=min(ans,4-(v1[i]+v2[i]+v1[n-1-i]+v2[n-1-i])+check(v1,v2,i+1,dp));

        return dp[i]=ans;
    }
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        int i=0,j=n-1;
        while(i<j){
                    vector<int>dp(m,-1);
            ans+=check(grid[i],grid[j],0,dp);
            i++;j--;
        }


            int extra=0,ct1=0,ct0=0;
        if(m%2==1){   
            int low=0,hi=n-1;
            while(low<=hi){
                if(grid[low][m/2]!=grid[hi][m/2])extra++;
                else if(grid[low][m/2]==1 && low!=hi)ct1+=2;
                else if(grid[low][m/2]==1 && low==hi)ct1+=1;
                else if(low!=hi)ct0+=2;
                else ct0+=1;
                low++,hi--;
            }
        }

            if(n%2==1){   
            int low=0,hi=m-1;
            while(low<=hi){

                if(grid[n/2][low]!=grid[n/2][hi])extra++;
                else if(grid[n/2][low]==1 && low!=hi)ct1+=2;
                else if(grid[n/2][low]==1 && low==hi)ct1+=1;
                else if(low!=hi)ct0+=2;
                else ct0+=1;
                low++,hi--;
            }
        }
        if(n%2==1 && m%2==1 && (grid[n/2][m/2]==1))ct1--;  
        if(n%2==1 && m%2==1 && (grid[n/2][m/2]==0))ct0--;

        ans+=extra;
        if(ct1%2==1)ans++,ct1--;
        if(extra==0 && ct1%4==2)return ans+2;
        if(extra%2==0)return ans;
        return ans;




    }
};
