class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])

        ret = 0
        for i in range(m // 2):
            for j in range(n // 2):
                k = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][n - 1 - j]
                ret += min(k, 4 - k)

        if m % 2 == 1:
            if n % 2 == 1:
                if grid[m // 2][n // 2]:
                    ret += 1

                diff = False

                k0 = 0
                for j in range(n // 2):
                    if grid[m // 2][j]:
                        k0 += 1

                    if grid[m // 2][j] != grid[m // 2][n - 1 - j]:
                        diff = True
                        ret += 1

                k1 = 0
                for i in range(m // 2):
                    if grid[i][n // 2]:
                        k1 += 1

                    if grid[i][n // 2] != grid[m - 1 - i][n // 2]:
                        diff = True
                        ret += 1

                if not diff and (k0 + k1) % 2 == 1:
                    ret += 2

            else:
                k = 0
                diff = False
                for j in range(n // 2):
                    if grid[m // 2][j]:
                        k += 1

                    if grid[m // 2][j] != grid[m // 2][n - 1 - j]:
                        diff = True
                        ret += 1

                if not diff and k % 2 == 1:
                    ret += 2

        elif n % 2 == 1:
            k = 0
            diff = False

            for i in range(m // 2):
                if grid[i][n // 2]:
                    k += 1

                if grid[i][n // 2] != grid[m - 1 - i][n // 2]:
                    diff = True
                    ret += 1

            if not diff and k % 2 == 1:
                ret += 2

        return ret
