class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        int flips = 0;

        // Check the center cell if both dimensions are odd
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                flips += 1;
            }
        }

        std::vector<int> shiftCounts(3, 0);

        // Count flips needed for rows if the number of rows is odd
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                shiftCounts[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        // Count flips needed for columns if the number of columns is odd
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                shiftCounts[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        // Update the answer based on shifts
        if (shiftCounts[2] % 2 == 1) {
            if (shiftCounts[1] > 0) {
                flips += shiftCounts[1];
            } else {
                flips += 2;
            }
        } else {
            flips += shiftCounts[1];
        }

        // Handle the corners of each 2x2 block
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4) {
                    flips += min(sum, 4 - sum);
                }
            }
        }

        return flips;
    }
};
