class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size(),count=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int c=0;
                c+=grid[i][j];
                c+=grid[i][n-j-1];
                c+=grid[m-i-1][j];
                c+=grid[m-i-1][n-j-1];
                count+=min(c,4-c);
            }
        }
        int ones=0,count1=0,count2=0;
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                count1+=(grid[m/2][i]!=grid[m/2][n-i-1]);
                if(grid[m/2][i]==grid[m/2][n-i-1]&&grid[m/2][i]==1)ones+=1;
            }
        }
        if(n%2==1){
            for(int i=0;i<m/2;i++){
                count2+=(grid[i][n/2]!=grid[m-i-1][n/2]);
                if(grid[i][n/2]==grid[m-i-1][n/2]&&grid[i][n/2]==1)ones+=1;
            }
        }
        count1+=count2;
        if(ones%2==1){
            if(count1==0)count1+=2;
        }
        if(n%2==1&&m%2==1){
            count+=grid[m/2][n/2];
        }
        return count+count1;
    }
};
