class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:

        NROW = len(grid)
        NCOL = len(grid[0])
        cnt = 0
        extra1s = 0
        wiggleroom = 0
        for r in range(NROW>>1):
            r2 = -1-r
            for c in range(NCOL>>1):
                c2 = -1-c
                if grid[r][c] != grid[r][c2]:
                    # we have to change 1, should we choose 0 or 1 ? which is best ?
                    # either way the number of 1 is divided by 4
                    changeA = 1
                    changeB = 1
                    if grid[r][c] != grid[r2][c]:
                        changeA += 1
                    else:
                        changeB += 1

                    if grid[r][c] != grid[r2][c2]:
                        changeA += 1
                    else:
                        changeB += 1

                    cnt += min(changeA,changeB)

                else:
                    if grid[r][c] != grid[r2][c]:
                        cnt += 1
                    if grid[r][c] != grid[r2][c2]:
                        cnt += 1

            if NCOL&1:
                c = NCOL>>1
                if grid[r][c] != grid[r2][c]:
                    wiggleroom += 2
                    cnt += 1
                elif grid[r][c] == 1:
                    extra1s += 2

        if NCOL&1 and NROW&1:
            c = NCOL>>1
            r = NROW>>1    
            if grid[r][c] == 1:
                grid[r][c] = 0 # mid cannot be 1
                cnt += 1

        if NROW&1: # when odd row
            r = NROW>>1    
            for c in range(NCOL>>1):
                c2 = -1-c
                if grid[r][c] != grid[r][c2]:
                    cnt += 1 
                    wiggleroom += 2
                else:
                    if grid[r][c] == 1:
                        extra1s += 2

        extra1s %= 4
        if extra1s > wiggleroom:
            cnt += extra1s
        return cnt
