class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        ll n = grid.size(), m = grid[0].size();
        ll ans = 0;
        vll s(3, 0);
        if (n % 2 && m % 2 && grid[n/2][m/2]==1) {
           ans++;
        }
        if (m % 2) {
            ll i = 0, j = n - 1;
            while (i < j) {
                s[grid[i++][m/2] + grid[j--][m/2]]++;
            }
        }
        if (n % 2) {
            ll i = 0, j = m - 1;
            while (i < j) {
                s[grid[n/2][i++] + grid[n/2][j--]]++;
            }
        }

        if (s[2]%2==0) {
            ans += s[1];
        } 
        else {
            if (s[1] > 0) {
                ans += s[1];
            } else {
                ans += 2;
            }
        }

        fl(i,n/2) {
            fl(j,m/2) {
                ll sum = grid[i][j] + grid[n-1-i][j] + grid[i][m-1-j] + grid[n-1-i][m-1-j];
                ans += (sum != 0 && sum != 4)?min(sum, 4 - sum):0;
            }
        }
        return ans;
    }
};
