class Solution {

    int calculate(vector<int> zeros, vector<int> ones)
    {
        vector<int> cnt(3,0);
        for(auto&x:ones)
        {
            cnt[x]++;
        }
        cnt[2]%=2;
        //throw out cnt[0] pairs
        int ans=cnt[1];//can make them 0 or 2 as per need
        if(cnt[2]==0)   return ans;
        if(cnt[1]>0)    return ans;
        return 2;
    }

public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();

        int ans=0;

        vector<int> Zeros;
        vector<int> Ones;

        for(int i=0;i<(m+1)/2;i++)
        {
            for(int j=0;j<(n+1)/2;j++)
            {
                int zeros=0;
                int ones=0;

                if(grid[i][j]==0)   zeros++;
                else                ones++;

                if(i!=m-1-i)
                {
                    if(grid[m-1-i][j]==0)   zeros++;
                    else                ones++;
                }

                if(j!=n-1-j)
                {
                    if(grid[i][n-1-j]==0)   zeros++;
                    else                ones++;
                }

                if(i!=m-1-i && j!=n-1-j)
                {
                    if(grid[m-1-i][n-1-j]==0)   zeros++;
                    else                ones++;
                }

                if(i==m-1-i &&j==n-1-j)
                {
                    ans+=ones;
                    continue;
                }

                if(i!=m-1-i && j!=n-1-j)
                {
                    ans+=min(zeros,ones);
                    continue;
                }

                Zeros.push_back(zeros);
                Ones.push_back(ones);
            }
        }

        return ans+calculate(Zeros,Ones);
    }
};
