class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int end;
        if(n%2 == 0){
          end = n/2 -1;  
        }else{
            end = n/2;
        }
        int flips = 0;
        int midFlips = 0;
        int intial_one = 0;
        int zero_count = 0;
        int colFli = 0;
        int col_one = 0;
        for(int i=0;i<=end;i++){
            int start = 0;
            int high = m-1;
            int op = n-i-1;
            if(op != i){
            while(start<=high){
            int sum = grid[i][start];
            sum += grid[op][start];
            if(start != high){
            sum += grid[i][high];
            sum += grid[op][high];
            flips += min(4-sum,sum);    
            }else{
                if(sum%2 != 0){
                    midFlips += 1;
                }else{
                        intial_one += sum;
                }
            }
                start++;
                high--;
            }
          }else{
             while(start<=high){
                 if(start == high){
                    if(grid[i][start] == 1){
                         intial_one += 1;
                     }else{
                         zero_count += 1;
                     } 
                 }
                 else{
                 if(grid[i][start] != grid[i][high]){
                     midFlips++;
                 }else{ 
                     if(grid[i][start] == 1){
                         intial_one += 2;
                     }else{
                         zero_count += 2;
                     }
                 }
                 }      
                 start++;
                 high--;
             }   
          }      
        }
        flips += midFlips;
        if(intial_one%4 != 0){
            if(intial_one%2 == 0){
            if(midFlips == 0){
                return flips+2;
            }
          }if(intial_one%4 == 1){
               return flips + 1;
            }else if(intial_one%4 == 3){
                if(midFlips>=1){
                    return flips+1;
                }else{
                    return flips+3;
                }
            }     
        }
        return flips;
    }
};
