class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
            int res=0,n=v.size(),m=v[0].size();
    for(int i=0;i<n/2;i++){
        for(int j=0;j<m/2;j++){
            int ct=(v[i][j]+v[n-1-i][j]+v[i][m-1-j]+v[n-1-i][m-1-j]);
            res+=min(ct,4-ct);
        }
    }
    // debug(res);
    int ct=0,ct1=0;
    if(n%2){
        for(int i=0;i<m/2;i++){
            if(v[n/2][i]==v[n/2][m-1-i]){
                if(v[n/2][i]) ct1+=2;
            }
            else ct1+=2,ct++;
        }
    }
    if(m%2){
        for(int i=0;i<n/2;i++){
            if(v[i][m/2]==v[n-1-i][m/2]){
                if(v[i][m/2]) ct1+=2;
            }
            else ct1+=2,ct++;
        }
    }
    // debug(res);
    if(n%2 && m%2) res+=v[n/2][m/2];
    if(ct) return res+ct;
    if(ct1%4) res+=(2+ct);
    return res;
    }
};
