class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        ret = 0
        if n % 2 == 1 and m % 2 == 1:
            if grid[n // 2][m // 2] == 1:
                ret += 1
        s = [0] * 3
        if n % 2 == 1:
            a, b = 0, m - 1
            while a < b:
                sm = grid[n // 2][a] + grid[n // 2][b]
                s[sm] += 1
                a += 1
                b -= 1
        if m % 2 == 1:
            a, b = 0, n - 1
            while a < b:
                sm = grid[a][m // 2] + grid[b][m // 2]
                s[sm] += 1
                a += 1
                b -= 1
        if s[2] % 2 == 1:
            if s[1] > 0:
                ret += s[1]
            else:
                ret += 2
        else:
            ret += s[1]
        for i in range(n // 2):
            for j in range(m // 2):
                a = grid[i][j]
                b = grid[n - 1 - i][j]
                c = grid[i][m - 1 - j]
                d = grid[n - 1 - i][m - 1 - j]

                sm = a + b + c + d
                if sm != 0 and sm != 4:
                    ret += min(sm, 4 - sm)

        return ret
