class Solution {
public:
	int minFlips(vector<vector<int>>& grid) {
		int n = grid.size();
		int m = grid[0].size();
	 	int cntl = 0,cntr = 0,ans = 0;
		for(int i = 0; i < (n+1) / 2; ++i) 
		{
			for(int j = 0; j < (m+1) / 2; ++j) 
			{
				set<array<int,2>> st;
				st.insert({i,j});
				st.insert({n-i-1,j});
				st.insert({n-i-1,m-j-1});
				st.insert({i,m-j-1});
				int l = 0,r = 0;
				for(auto [x,y] : st) 
				{
					if(grid[x][y] == 0) l++;
					else r++;
				}
				if(st.size() == 4) ans += min(4-l,4-r);
				else if(st.size() == 2) 
				{
					if(r == 2) cntl++;
					else if(r == 1) cntr++;
				}
				else ans += 1-l;
			}
		} 
		cntl %= 2;
		if(cntl && cntr >= 1) 
		{
			cntl = 0;
			cntr -= 1;
            ans++;
		}
		ans += cntl * 2 + cntr;
		return ans;
	}
};
