class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        rowcol=0
        total=0
        onenum=0
        twonum=0
        threenum=0
        for i in range(len(grid)):
            total+=sum(grid[i])
        for i in range(len(grid)//2):
            for j in range(len(grid[0])//2):
                tmpnum=grid[i][j]+grid[i][len(grid[0])-1-j]+grid[len(grid)-1-i][j]+grid[len(grid)-1-i][len(grid[0])-1-j]
                if tmpnum==1:
                    onenum+=1
                elif tmpnum==2:
                    twonum+=1
                elif tmpnum==3:
                    threenum+=1
        if len(grid)%2:
            for j in range(len(grid[0])//2):
                if grid[len(grid)//2][j]!=grid[len(grid)//2][len(grid[0])-1-j]:
                    rowcol+=1
        if len(grid[0])%2:
            for i in range(len(grid)//2):
                if grid[i][len(grid[0])//2]!=grid[len(grid)-1-i][len(grid[0])//2]:
                    rowcol+=1
        if (total-onenum+threenum+rowcol+2*twonum)%4==1:
            return onenum+2*twonum+threenum+rowcol+1
        if (total-onenum+threenum+rowcol+2*twonum)%4==3:
            if rowcol:
                return onenum+2*twonum+threenum+rowcol+1
            else:
                return onenum+2*twonum+threenum+rowcol+3

        elif (twonum*2+threenum+rowcol+total-onenum)%4:
            if rowcol:
                return onenum+twonum*2+threenum+rowcol
            else:
                return onenum+twonum*2+threenum+rowcol+2
            # 000
            # 110
            # 011
            # 001

        return onenum+twonum*2+threenum+rowcol
