class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid[0].size();
        int m=grid.size();
        int co=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int a=grid[i][j],b=grid[i][n-j-1],c=grid[m-i-1][j],d=grid[m-i-1][n-j-1];
                int p=0;
                if(a==1) p++;
                if(b==1) p++;if(c==1) p++;if(d==1) p++;
                if(p==1||p==3) co++;
                if(p==2) co=co+2;
            }
        }
        int x=0,y=0;
        if(m%2==1){

            for(int i=0;i<n/2;i++){
                if(grid[m/2][i]!=grid[m/2][n-i-1]) x++;
                if(grid[m/2][i]==1&&grid[m/2][n-i-1]==1) y++;
            }
        }
        if(n%2==1){

            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]) x++;
                if(grid[i][n/2]==1&&grid[m-i-1][n/2]==1) y++;
            }
        }
        int ans=co+x;
        if(n%2==1&&m%2==1&&grid[m/2][n/2]==1){
            ans++;
        }
        if(y%2==1&&x==0){
            return ans+2;
        }else{
            return ans;
        }

    } 

};
