class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),i,j,ans=0,x=0,y=0,z=0;
        for(i=0;i<n/2;i++)
        {
            for(j=0;j<m/2;j++)
            {
                vector<int> freq(2);
                freq[grid[i][j]]++;
                freq[grid[i][m-1-j]]++;
                freq[grid[n-1-i][j]]++;
                freq[grid[n-1-i][m-1-j]]++;
                ans+=min(freq[0],freq[1]);
            }
        }
        if(n%2)
        {
            for(j=0;j<m/2;j++)
            {
                if(grid[n/2][j]!=grid[n/2][m-1-j])
                    x++;
                else if(grid[n/2][j])
                    y+=2;
            }
        }
        if(m%2)
        {
            for(i=0;i<n/2;i++)
            {
                if(grid[i][m/2]!=grid[n-1-i][m/2])
                    x++;
                else if(grid[i][m/2])
                    y+=2;
            }
        }
        if(n%2 && m%2)
        {
            if(grid[n/2][m/2])
                z=2;
            else
                z=1;
        }
        y%=4;
        //cout<<x<<" "<<y<<" "<<z<<" "<<ans<<endl;
        ans+=x;

        if(y==0)
        {
            if(z==2)
                ans++;
        }
        else if(y==2)
        {
            if(x>0 && z==2)
            ans++;
            else if(x==0)
            {
                if(z==2)
                    ans+=3;
                else
                    ans+=2;
            }
        }
        return ans;
    }
};
