class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int c=grid[i][j];
                c+=grid[n-1-i][j];
                c+=grid[i][m-1-j];
                c+=grid[n-1-i][m-1-j];
                int mini=min(c,4-c);
                ans+=mini;
            }
        }
        // cout<<ans<<endl;
        if(n%2!=0&&m%2!=0){
            if(grid[n/2][m/2])ans++;
              int one=0;
              int ext_one=0;
              for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-1-j]){
                    ext_one+=2;
                    ans++;
                }
                else if(grid[n/2][j]==1)one+=2;
              }
              for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]){
                    ext_one+=2;
                    ans++;
                }
                else if(grid[i][m/2])one+=2;
              }
              if((one%4)==0)return ans;
              else {
                   if(ext_one!=0)return ans;
                   return ans+=2;
             }
        }
        else if(n%2!=0){
              int one=0;
              int ext_one=0;
              int zero=0;
              for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-1-j]){
                    ext_one+=2;
                    ans++;
                }
                else if(grid[n/2][j]==1)one+=2;
                else zero+=2;
              }
              if((one%4)==0)return ans;
              else {
                   if(ext_one!=0)return ans;
                   return ans+=2;
             }
        }
        else if(m%2!=0){
            // cout<<-1<<endl;
              int one=0;
              int ext_one=0;
              for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]){
                    ext_one+=2;
                    ans++;
                }
                else if(grid[i][m/2])one+=2;
              }
              if((one%4)==0)return ans;
              else {
                   if(ext_one!=0)return ans;
                   return ans+=2;
             }
        }
        else{
            return ans;
        }
    }
};
