class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), totalcount = 0;
        for(int i = 0; i < n/2; i++) {
            for(int j = 0; j < m/2; j++) {
                int count = 0;
                if(grid[i][j] == 1) {
                    count++;
                }
                if(grid[i][m - j - 1] == 1) {
                    count++;
                }
                if(grid[n - i - 1][j] == 1) {
                    count++;
                }
                if(grid[n- i - 1][m - j - 1] == 1) {
                    count++;
                }
                totalcount += min(count, 4 - count);
            }
        }
        int pairs = 0;
        bool change1 = 0, change2 = 0;
        if(m % 2) {
            for(int i = 0; i < n/2; i++) {
                if(grid[i][m/2] != grid[n - i - 1][m/2]) {
                    totalcount++;
                    change1 = true;
                }
                else if(grid[i][m/2] == 1) {
                    pairs++;
                }
            }
        }
        if(n % 2) {
            for(int i = 0; i < m/2; i++) {
                if(grid[n/2][i] != grid[n/2][m - i - 1]) {
                    totalcount++;
                    change2 = true;
                }
                else if(grid[n/2][i] == 1) {
                    pairs++;
                }
            }
        }
        if(pairs % 2 == 1 && !change1 && !change2) {
            totalcount += 2;
        }

        if(m % 2 == 1 && n % 2 == 1 && grid[n/2][m/2]) {
            totalcount++;
        }

        return totalcount;
    }
};
