class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        res = 0
        if n % 2 == 1 and m % 2 == 1:
            if grid[n // 2][m // 2] == 1:
                res += 1

        S = [0, 0, 0]
        if n % 2 == 1:
            a, b = 0, m - 1
            while a < b:
                sm = grid[n // 2][a] + grid[n // 2][b]
                S[sm] += 1
                a += 1
                b -= 1

        if m % 2 == 1:
            a, b = 0, n - 1
            while a < b:
                sm = grid[a][m // 2] + grid[b][m // 2]
                S[sm] += 1
                a += 1
                b -= 1

        if S[2] % 2 == 1:
            if S[1] > 0:
                res += S[1]
            else:
                res += 2
        else:
            res += S[1]

        for i in range(n // 2):
            for j in range(m // 2):
                a = grid[i][j]
                b = grid[n - 1 - i][j]
                c = grid[i][m - 1 - j]
                d = grid[n - 1 - i][m - 1 - j]

                sm = a + b + c + d

                if sm != 0 and sm != 4:
                    res += min(sm, 4 - sm)

        return res
