class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int stR = 0;
        int endR = grid.size()-1;
        int result = 0;
        int ones = 0;
        bool z2o = false;
        while(stR<endR){
            int stC = 0;
            int endC = grid[0].size()-1;
            while(stC<endC){
                int o = grid[stR][stC] + grid[stR][endC] + grid[endR][stC] + grid[endR][endC];
                if(o<=2){
                    result += o;
                }
                else{
                    result += (4-o);
                }
                stC++;
                endC--;
            }
            if(stC==endC){
                if(grid[stR][stC]!=grid[endR][endC]){
                    result++;
                    if(ones%4!=0){
                        ones += 2;
                    }
                    z2o = true;
                }
                else{
                    if(grid[stR][stC]==1){
                        ones += 2;
                    }
                }
            }
            stR++;
            endR--;
        } 
        if(stR==endR){
            int stC = 0;
            int endC = grid[0].size()-1;
            while(stC<endC){
                if(grid[stR][stC]!=grid[stR][endC]){
                    result++;
                    if(ones%4!=0){
                        ones += 2;
                    }
                    z2o = true;
                }
                else{
                    if(grid[stR][stC]==1){
                        ones += 2;
                    }
                }
                stC++;
                endC--;
            }
            if(stC==endC && grid[stR][stC]==1){
                result++;
            }
        } 
        if(ones%4!=0 && !(z2o)){
            result += 2;
        } 
        return result;  
    }
};
