class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        self.special = 0
        vis = set()
        def check(r1, c1):
            r2 = ROW - r1 - 1
            c2 = COL - c1 - 1
            if (r1,c1) in vis or (r1,c2) in vis or (r2,c1) in vis or (r2,c2) in vis:
                return
            vis.add((r1,c1))
            if r1 == r2 and c1 == c2:
                return 0
            if r1 == r2:
                if grid[r1][c1] == grid[r1][c2]:
                    return 0
                self.special += 1
                grid[r1][c1] = 1
                grid[r1][c2] = 1
                return 1

            if c1 == c2:
                if grid[r1][c1] == grid[r2][c1]:
                    return 0
                self.special += 1
                grid[r1][c1] = 1
                grid[r2][c1] = 1
                return 1

            ones = 0
            for i in (r1,r2):
                for j in (c1,c2):
                    ones += grid[i][j]
            zeros = 4 - ones
            res = 0 if zeros > ones else 1
            for i in (r1,r2):
                for j in (c1,c2):
                    grid[i][j] = res

            return min(zeros, ones)




