class Solution {
public:

    int check(vector<int> v){
        int n = v.size();
        int i = 0;
        int j = n-1;
        int res = 0;
        while(i <= j){
            if(v[i]!= v[j] ){
                res++;
            }
            else if(v[i] == 1 && v[j] == 1){
                if(i!= j)res+=2;
                else res++;
            }
            i++;
            j--;
        }
        return res;
    }

    int minFlips(vector<vector<int>>& grid) {

        int ans=0;
        int a1=0;
        int n=grid.size();
        int m=grid[0].size();
        int a2=0;

        vector<int> v;
        for(int i = 0;i<n;i++){
            v.push_back(1);
        }
        vector<vector<int>>vis(n,vector<int>(m,0));

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(!vis[i][j]){

                    vis[i][j]=1;

                    vis[n-1-i][j]=1;

                    vis[i][m-1-j]=1;

                    vis[n-1-i][m-1-j]=1;

                    if(i==n-1-i && j==m-1-j) {

                        if(grid[i][j]==1) ans++;
                    }
                    else if(i==n-1-i){
                        int b=grid[i][j]+grid[i][m-1-j];
                        if(b==1){
                            ans++;
                            a2++;
                        }
                        else if(b==2) a1+=2;
                    }
                    else if(j==m-1-j){
                        int b=grid[n-1-i][j]+grid[i][j];
                        if(b==1){
                            ans++;
                            a2++;
                        }
                        else if(b==2) a1+=2;
                    }
                    else{
                int b=grid[i][j]+grid[n-1-i][j]+grid[i][m-1-j]+grid[n-1-i][m-1-j];
                if(b==1){
                    ans++;
                }
                else if(b==2){
                    ans+=2;
                }
                else if(b==3) {
                    ans+=1;
                }
                }
            }
        }
        }
        if(a1%4!=0 && a2==0) ans+=2; 

        return ans;
    }
};
